<?php
namespace Elementor;

class custom_description extends Widget_Base {

	public function get_name() {
		return 'Description';
	}
	
	public function get_title() {
		return 'Traction - Logo with Description';
	}
	
	public function get_icon() {
		return 'fa fa-building';
	}
	
	public function get_categories() {
		return [ 'trc-category' ];
	}
	protected function _register_controls() {
        $args = array(
            'post_type' => 'page',
            'post_status'   => 'publish'
        );
        $pages = new \WP_Query($args);
        $output = '';
        foreach($pages as $page){
            $output .= $page;
        }
		$this->start_controls_section(
			'section_content',
			[
				'label' => __( 'Traction - Description', 'elementor' ),
			]
        );
        $this->add_control(
			'description',
			[
				'label' => __( 'Description', 'elementor' ),
                'type' => \Elementor\Controls_Manager::TEXTAREA,
                'rows'  => 10,
				'placeholder' => __( 'Type your description here', 'elementor' ),
			]
        );
        $this->add_control(
			'show_logo',
			[
				'label' => __( 'Show Logo', 'elementor' ),
				'type' => \Elementor\Controls_Manager::SWITCHER,
				'label_on' => __( 'Show', 'elementor' ),
				'label_off' => __( 'Hide', 'elementor' ),
				'return_value' => 'yes',
				'default' => 'yes',
			]
        );
         $this->add_control(
			'link',
			[
				'label' => __( 'Page Links', 'elementor' ),
				'type' => \Elementor\Controls_Manager::SELECT,
				'options' => [$pages],
			]
		);
        $this->add_control(
			'link_title',
			[
				'label' => __( 'Link Title', 'elementor' ),
				'type' => \Elementor\Controls_Manager::TEXT,
			]
		);

        $this->end_controls_section();
    
		// $this->start_controls_section(
		// 	'content_section',
		// 	[
		// 		'label' => __( 'Quality Approved Logos', 'plugin-name' ),
		// 		'tab' => \Elementor\Controls_Manager::TAB_CONTENT,
		// 	]
		// );

		// $this->add_control(
		// 	'gallery',
		// 	[
		// 		'label' => __( 'Add Images', 'plugin-domain' ),
		// 		'type' => \Elementor\Controls_Manager::GALLERY,
		// 		'default' => [],
		// 	]
		// );

		// $this->end_controls_section();

	}
	
